ee:message.ee:set-payload:
%dw 2.0
output application/xml

ns soapenv http://schemas.xmlsoap.org/soap/envelope/
ns flig http://schemas.15below.com/flightTrigger.criteria
ns fif http://schemas.datacontract.org/2004/07/FifteenBelow.FlightStatus.FlightTriggerObjects
var flightStatusEvent = (readUrl("classpath://FlightStatusEventMapping.json","application/json"))
---
{
    soapenv#Envelope:
    {     
        soapenv#Body:
            flig#NewFlightTrigger: 
            {
                flig#flightStatus:
                {
                    fif#ChangeDetail:  
                    {
                        fif#ChangeEvent: (flightStatusEvent filter($.JMSEventType == vars.JMSEventType))[0].FlightStatusEvent,
                        fif#ChangedFlightLeg: payload.impactedLeg as String,
                        fif#NewValue: null
                    },
                    fif#FlightInformation:{
                        fif#CarrierCode: "G4",
                        fif#DepartureDate: payload.flightInfo.legInfo.departureDateTime[0].scheduledUTC,
                        fif#FlightLegs: (payload.flightInfo.legInfo map
                        {
                            fif#FSFlightLeg:
                            {
                                fif#FSFlightArrival: 
                                {
                                    fif#AirportCode: $.arrivalAirport.locationCode,
                                    fif#Estimated: $.arrivalDateTime.estimatedUTC,
                                    fif#Scheduled: $.arrivalDateTime.scheduledUTC
                                },
                                fif#FSFlightDeparture: {                                                                 fif#AirportCode: $.departureAirport.locationCode,
                                    fif#Estimated: $.departureDateTime.estimatedUTC,
                                    fif#Scheduled: $.departureDateTime.scheduledUTC
                                },  
                                fif#FSFlightLegState:
                                {
                                    fif#Cancelled: $.cancelled,
                                    fif#Diverted: false,
                                    fif#Scheduled: true
                                },
                                fif#FlightLeg: $.nbr                   

                            }
                        }) distinctBy $,
                    fif#FlightNumber: payload.flightInfo.nbr
                },
                fif#JobID: attributes.headers."JMSCorrelationID"
            }        
        }
    }
}
